import React, { Component } from "react";
import axios from "axios";
import "./LandingPage.css";

export class WatchDetails extends Component {
  state = {
    watch: {}
  };

  componentDidMount() {
    this.getWatch();
  }
  getWatch = () => {
    const { id } = this.props.match.params;
    axios
      .get(`http://localhost:6677/api/watches/${id}`)
      .then(data => this.setState({ watch: data.data }));
  };

  render() {
    const {
      title,
      description,
      image_path,
      price,
      quantity
    } = this.state.watch;
    console.log(this.state.watch);
    return (
      <div className="container">
        <div className="hook">
          <h1>{title}</h1>
          <p>
            <i>{description}</i>
          </p>
          <h2 style={{ marginTop: "20px" }}>$ {price}</h2>
          <button disabled>ADD TO CART</button>
        </div>
        <div
          className="image"
          style={{ backgroundImage: `url(${image_path})` }}
        ></div>
      </div>
    );
  }
}

export default WatchDetails;
