import React, { Component } from "react";
import "./Navigation.css";
import axios from "axios";
export class Navigation extends Component {
  state = {
    showHamburger: false,
    showLogin: false,
    loginEmail: "",
    loginPassword: ""
  };
  handleLogin = e => {
    this.setState({ showLogin: !this.state.showLogin });
  };
  handleHamburger = e => {
    this.setState({ showHamburger: !this.state.showHamburger });
  };
  onTextChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onLogin = e => {
    e.preventDefault();

    const loginInfo = {
      email: this.state.loginEmail,
      password: this.state.loginPassword
    };

    axios
      .post("http://localhost:6677/api/auth", loginInfo)
      .then(res => {
        // console.log(res);
        const token = res.headers.x - auth - token;
        localStorage.setItem("jwtToken", token);
      })
      .catch(err => console.log(err.response.data));
  };
  render() {
    return (
      <nav
        className={`menu wow fadeInDown ${
          this.state.showHamburger ? "change" : null
        } `}
      >
        <a
          className={`logo menu ${this.state.showHamburger ? "change" : null}`}
          href="/"
        >
          <i className="far fa-clock"></i> <h3>Watchful</h3>
        </a>

        <ul
          className={`nav  nav-1 menu ${
            this.state.showHamburger ? "change" : null
          }`}
        ></ul>
        <ul
          className={`nav  nav-2 menu ${
            this.state.showHamburger ? "change" : null
          }`}
        >
          <li>
            <a className="register" href>
              Register
            </a>
          </li>
          <li onClick={this.showLogin}>
            <a
              className="login"
              href
              onClick={this.handleLogin}
              style={{ position: "relative" }}
            >
              Login
            </a>
            {this.state.showLogin ? (
              <form className="login-form" onSubmit={this.onLogin}>
                <input
                  name="loginEmail"
                  value={this.state.loginEmail}
                  type="email"
                  placeholder="Email"
                  onChange={this.onTextChange}
                />
                <input
                  name="loginPassword"
                  value={this.state.loginPassword}
                  type="password"
                  placeholder="Password"
                  onChange={this.onTextChange}
                />
                <button type="submit">Submit</button>
              </form>
            ) : null}
          </li>
          <li>
            <a href="/">
              <i className="fas fa-shopping-cart"></i>
            </a>
          </li>
          <li>
            <a href="/">
              <i className="fas fa-search"></i>
            </a>
          </li>
        </ul>
        <div
          className={`hamburger ${this.state.showHamburger ? "change" : null}`}
          onClick={this.handleHamburger}
        >
          <div className="bar1"></div>
          <div className="bar2"></div>
          <div className="bar3"></div>
        </div>
      </nav>
    );
  }
}

export default Navigation;
